import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { IoIosArrowDown } from "react-icons/io";
import { Link } from "react-scroll";
import { useSpring, animated, config } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {

    const AnimatedLink = animated(Link)
    const AnimatedIcon = animated(IoIosArrowDown)
    const [isHover, setIsHover] = useState(false);

    const navArrow = useSpring({
        size: isHover ? "100px" : "50px",
        opacity: isHover? 1 : 0,
        config: config.stiff
    });

    function toggleIsHover() {
        (setIsHover(!isHover))
    }

    return (
            <div style={{ marginTop: "60px" }}>
            <AnimatedLink
                onMouseEnter={toggleIsHover}
                onMouseLeave={toggleIsHover}
                to={scrollDestination}
                smooth={true}
                duration={500}
                ignoreCancelEvents={false}
            >
                <AnimatedIcon
                    style={{ color: colors.white, size: navArrow.size, cursor: "pointer", marginLeft: "30px" }}
                ></AnimatedIcon>
            </AnimatedLink>

            <animated.div
                style={{
                    justifyContent: "row",
                    opacity: navArrow.opacity,
                }}
            >
                <h4 style={{ color: colors.white, marginLeft: "20px" }}>{destinationName}</h4>
            </animated.div>
        </div> 
    )
}

export default NavArrowDown
