import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { BsArrowDownCircle } from "react-icons/bs";
import { Link } from "react-scroll";
import { useSpring, animated, config } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {
    const [isHover, setIsHover] = useState(false);

    const navArrow = useSpring({
        size: isHover ? "50px" : "30px",
        opacity: isHover? 1 : 0
    })


    return (
            <animated.div style={{ navArrow }}>
                <Link
                    to={scrollDestination}
                    smooth={true}
                    // onMouseEnter={setIsHover(true)}
                    // onMouseLeave={setIsHover(false)}
                    duration={500}
                    ignoreCancelEvents={false}
                >
                    <BsArrowDownCircle
                    color={colors.white}
                    style={{ cursor: "pointer", marginLeft: "30px", overflow: "hidden", size: navArrow.size }}
                    ></BsArrowDownCircle>
                </Link>
                <h4 style={{ color: colors.white, marginLeft: "20px"}}>{destinationName}</h4>
            </animated.div>
    )
}

export default NavArrowDown
