import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { BsArrowDownCircle } from "react-icons/bs";
import Link from "react-scroll";
import { Spring, animated } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {
    const [isHover, setIsHover] = useState(false);

    function toggleHover() {
        setIsHover(!isHover)
    }
    return (
        <Spring
          to={{
              size: isHover ? "50px" : "30px"
          }}
        >
            {styles => <animated.div style={styles}>
                <Link
                  to={scrollDestination}
                  smooth={true}
                  onSetActive={toggleHover}
                  onSetInactive={toggleHover}
                  duration={500}
                  ignoreCancelEvents={false}
                >
                    <BsArrowDownCircle
                      color={colors.white}
                      size="30px"
                      style={{ cursor: "pointer", marginLeft: "30px", marginTop: "30px" }}
                    ></BsArrowDownCircle>
                </Link>
                <p color={colors.white}>{destinationName}</p>
            </animated.div>}
        </Spring>
    )
}

export default NavArrowDown
