import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { BsArrowDownCircle } from "react-icons/bs";
import { Link } from "react-scroll";
import { Spring, animated } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {
    const [isHover, setIsHover] = useState(false);

    function toggleHover() {
        setIsHover(!isHover);
    }

    return (
        <Spring
          to={{
              size: isHover ? "40px" : "30px",
              opacity: isHover ? 1 : 0,
          }}
        >
            { props => (
                <animated.div style={{ justifyContent: "row", display: "inline-flex", marginTop: "60px"}}>
                    <Link
                    to={scrollDestination}
                    smooth={true}
                    onMouseEnter={setIsHover(true)}
                    onMouseLeave={setIsHover(false)}
                    duration={500}
                    ignoreCancelEvents={false}
                    >
                        <BsArrowDownCircle
                        color={colors.white}
                        size="30px"
                        style={{ cursor: "pointer", marginLeft: "30px", overflow: "hidden" }}
                        ></BsArrowDownCircle>
                    </Link>
                    <h4 style={{ color: colors.white, marginLeft: "20px", opacity: props.opacity }}>{destinationName}</h4>
                </animated.div>)
            }
        </Spring>
    )
}

export default NavArrowDown
