import React, { useState } from "react";
import  { colors } from "../../styling/colors";
import SpringBar from "./SpringBar";
import { FaLinkedin, FaInstagram } from "react-icons/fa";
import { AiOutlineMail, AiFillGithub } from "react-icons/ai";
import { Trail, config } from "react-spring";

function Welcome({

}) {
    const [springDone, setSpringDone] = useState(false);

    // Create array for trail animation
    const bars = [];
    for (let i = 0; i < 10; i++) {
        bars.push(
            <SpringBar barWidth="10vw" barColor={colors.green} key={i}></SpringBar>
        )
    }

    return (
        <div
          className="full_section"
          style={{ backgroundColor: colors.white }}
        >
            {/* <div className="section_main_content">
                <h1> Hi This Is Landing Component </h1>
            </div> */}

            { springDone ? (
                <div 
                  style={{ 
                    backgroundColor: colors.blue,
                    width: "100vw",
                    height: "100vh"
                }}
                >
                </div>

             ) : (
                <Trail
                  items={bars}
                  from={{ marginTop: "-100vh" }}
                  to={{ marginTop: "-50vh" }}
                  keys={(item) => item.key }
                  config={config.gentle}
                  onRest={() => setTimeout(() => setSpringDone(false), 1000)}
                >
                    {(item) => (props) => (<div style={props}>{item}</div>)}
                </Trail>
             )
            }
        </div>
    )
}

export default Welcome
