import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { BsArrowDownCircle } from "react-icons/bs";
import { Link } from "react-scroll";
import { useSpring, animated, config } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {
    const [isHover, setIsHover] = useState(false);

    const navArrow = useSpring({
        from: {
            size: "30px",
            opacity: 0
        },
        to: {
            size: "50px",
            opacity: 1
        }
    })

    function toggleHover() {
        setIsHover(!isHover);
    }

    return (
            <animated.div style={{ justifyContent: "row", display: "inline-flex", marginTop: "60px", navArrow}}>
                <Link
                    activeClass="active"
                    to={scrollDestination}
                    smooth={true}
                    onMouseEnter={toggleHover()}
                    onMouseLeave={toggleHover()}
                    duration={500}
                    ignoreCancelEvents={false}
                >
                    <BsArrowDownCircle
                    color={colors.white}
                    style={{ cursor: "pointer", marginLeft: "30px", overflow: "hidden" }}
                    ></BsArrowDownCircle>
                </Link>
                <h4 style={{ color: colors.white, marginLeft: "20px"}}>{destinationName}</h4>
            </animated.div>
    )
}

export default NavArrowDown
