import React, { useState } from "react";
import { colors } from "./../../styling/colors"
import { BsArrowDownCircle } from "react-icons/bs";
import { Link } from "react-scroll";
import { Spring, animated, config } from  "react-spring";

function NavArrowDown({ 
    scrollDestination,
    destinationName
}) {
    const [isHover, setIsHover] = useState(false);

    function toggleHover() {
        setIsHover(!isHover);
    }

    return (
        <Spring
            size={isHover ? "50px" : "30px"}
            opacity={isHover ? 1 : 0}
            config={config.gentle}
        >
            { (styles) => (
                <animated.div style={{ justifyContent: "row", display: "inline-flex", marginTop: "60px", styles}}>
                    <Link
                    activeClass="active"
                    to={scrollDestination}
                    smooth={true}
                    onMouseEnter={toggleHover}
                    onMouseLeave={toggleHover}
                    duration={500}
                    ignoreCancelEvents={false}
                    >
                        <BsArrowDownCircle
                        color={colors.white}
                        size={styles.size}
                        style={{ cursor: "pointer", marginLeft: "30px", overflow: "hidden", styles}}
                        ></BsArrowDownCircle>
                    </Link>
                    <h4 style={{ color: colors.white, marginLeft: "20px", opacity: styles.opacity }}>{destinationName}</h4>
                </animated.div>)
            }
        </Spring>
    )
}

export default NavArrowDown
